import { useEffect, useState } from "react";
import API from "../api";
import ProductCard from "../components/ProductCard";

export default function ProductCatalog() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [filterAvailable, setFilterAvailable] = useState(null); // null = all, true = in stock, false = out of stock
  const [filterOnOffer, setFilterOnOffer] = useState(null);     // null = all, true = on offer, false = not on offer
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      setError(null);
      const res = await API.get("/products");

      const normalizedProducts = res.data.map((p) => ({
        ...p,
        available: p.available === true || p.available === "true" || p.available === 1,
        onOffer: p.onOffer === true || p.onOffer === "true" || p.onOffer === 1,
      }));

      setProducts(normalizedProducts);
    } catch (error) {
      console.error("Error fetching products:", error);
      setError(`Failed to load products: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  // Filtering logic
  const filteredProducts = products.filter((product) => {
    const matchesSearch =
      product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (product.description && product.description.toLowerCase().includes(searchTerm.toLowerCase()));

    const matchesAvailable =
      filterAvailable === null || product.available === filterAvailable;

    const matchesOnOffer =
      filterOnOffer === null || product.onOffer === filterOnOffer;

    return matchesSearch && matchesAvailable && matchesOnOffer;
  });

  // Counts
  const inStockCount = products.filter((p) => p.available).length;
  const outOfStockCount = products.filter((p) => !p.available).length;
  const onOfferCount = products.filter((p) => p.onOffer).length;
  const notOnOfferCount = products.filter((p) => !p.onOffer).length;

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="p-8 text-center bg-white shadow-lg rounded-2xl">
        <h1 className="text-4xl font-bold">Welcome to Nadir Brothers</h1>
        <div className="flex justify-center gap-4 mt-4">
          <div>üì¶ {products.length} Total Products</div>
          <div>‚úÖ {inStockCount} In Stock</div>
          <div>‚ùå {outOfStockCount} Out of Stock</div>
          <div>üè∑Ô∏è {onOfferCount} On Offer</div>
          <div>üí∞ {notOnOfferCount} Not on Offer</div>
        </div>
      </div>

      {/* Filters */}
      <div className="flex flex-col flex-wrap items-center justify-center gap-4 p-6 bg-white shadow-lg rounded-2xl md:flex-row">
        <input
          type="text"
          placeholder="Search products..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="flex-1 px-4 py-2 border rounded-xl"
        />

        {/* Available / Out of Stock */}
        <div className="flex items-center gap-2">
          <label>
            <input
              type="radio"
              name="availableFilter"
              checked={filterAvailable === null}
              onChange={() => setFilterAvailable(null)}
            />
            All
          </label>
          <label>
            <input
              type="radio"
              name="availableFilter"
              checked={filterAvailable === true}
              onChange={() => setFilterAvailable(true)}
            />
            In Stock
          </label>
          <label>
            <input
              type="radio"
              name="availableFilter"
              checked={filterAvailable === false}
              onChange={() => setFilterAvailable(false)}
            />
            Out of Stock
          </label>
        </div>

        {/* On Offer / Not on Offer */}
        <div className="flex items-center gap-2">
          <label>
            <input
              type="radio"
              name="offerFilter"
              checked={filterOnOffer === null}
              onChange={() => setFilterOnOffer(null)}
            />
            All
          </label>
          <label>
            <input
              type="radio"
              name="offerFilter"
              checked={filterOnOffer === true}
              onChange={() => setFilterOnOffer(true)}
            />
            On Offer
          </label>
          <label>
            <input
              type="radio"
              name="offerFilter"
              checked={filterOnOffer === false}
              onChange={() => setFilterOnOffer(false)}
            />
            Not on Offer
          </label>
        </div>
      </div>

      {/* Products */}
      <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4">
        {filteredProducts.map((product) => (
          <ProductCard key={product._id} product={product} />
        ))}
      </div>
    </div>
  );
}
