import { useEffect, useState } from "react";
import API from "../api";
import ProductCard from "../components/ProductCard";

export default function ProductCatalog() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [showAvailableOnly, setShowAvailableOnly] = useState(false);
  const [error, setError] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const productsPerPage = 20;

  useEffect(() => {
    console.log("üöÄ ProductCatalog component mounted, fetching products...");
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      setError(null);
      console.log("üì° Fetching products from API...");
      const res = await API.get("/products");
      console.log("üì¶ Products received:", res.data);
      console.log("üì¶ Number of products:", res.data?.length || 0);
      setProducts(res.data || []);
    } catch (error) {
      console.error("‚ùå Error fetching products:", error);
      setError(`Failed to load products: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  const filteredProducts = products.filter(product => {
    const matchesSearch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (product.description && product.description.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesAvailability = !showAvailableOnly || product.isAvailable;
    return matchesSearch && matchesAvailability;
  });

  // Pagination calculations
  const totalPages = Math.ceil(filteredProducts.length / productsPerPage);
  const startIndex = (currentPage - 1) * productsPerPage;
  const endIndex = startIndex + productsPerPage;
  const currentProducts = filteredProducts.slice(startIndex, endIndex);

  const availableCount = products.filter(p => p.isAvailable).length;

  // Reset to first page when filters change
  useEffect(() => {
    setCurrentPage(1);
  }, [searchTerm, showAvailableOnly]);

  if (loading) {
    return (
      <div className="flex flex-col items-center justify-center h-64">
        <div className="animate-spin rounded-full h-16 w-16 border-b-4 border-indigo-600 mb-4"></div>
        <p className="text-gray-600 font-medium">Loading our amazing products...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-xl p-8 text-center">
        <div className="text-6xl mb-4">‚ö†Ô∏è</div>
        <h2 className="text-2xl font-bold text-red-800 mb-2">Connection Error</h2>
        <p className="text-red-600 mb-4">{error}</p>
        <div className="space-y-2 text-sm text-red-600 mb-4">
          <p>Possible solutions:</p>
          <ul className="list-disc list-inside space-y-1">
            <li>Make sure backend is running on http://localhost:5000</li>
            <li>Check if MongoDB Atlas is connected</li>
            <li>Verify CORS settings in backend</li>
          </ul>
        </div>
        <button
          onClick={fetchProducts}
          className="bg-red-600 text-white px-6 py-2 rounded-lg hover:bg-red-700 transition-colors"
        >
          Try Again
        </button>
      </div>
    );
  }

  console.log("üé® Rendering ProductCatalog with", products.length, "products");

  return (
    <div className="space-y-8">
      {/* Welcome Section */}
      <div className="text-center bg-white rounded-2xl shadow-lg p-8">
        <h1 className="text-4xl font-bold text-gray-900 mb-4">
          Welcome to Nadir Brothers
        </h1>
        <p className="text-xl text-gray-600 mb-6">
          Discover our amazing collection of quality products
        </p>
        <div className="flex justify-center space-x-8 text-sm font-medium">
          <div className="bg-blue-50 px-4 py-2 rounded-lg">
            <span className="text-blue-600">üì¶ {products.length} Total Products</span>
          </div>
          <div className="bg-green-50 px-4 py-2 rounded-lg">
            <span className="text-green-600">‚úÖ {availableCount} Available Now</span>
          </div>
        </div>
      </div>

      {/* Search and Filter Section */}
      <div className="bg-white rounded-2xl shadow-lg p-6">
        <div className="flex flex-col md:flex-row gap-4 items-center">
          <div className="flex-1 relative">
            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
              <span className="text-gray-400 text-xl">üîç</span>
            </div>
            <input
              type="text"
              placeholder="Search for products..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent text-gray-900"
            />
          </div>
          
          <div className="flex items-center space-x-3">
            <input
              type="checkbox"
              id="availableOnly"
              checked={showAvailableOnly}
              onChange={(e) => setShowAvailableOnly(e.target.checked)}
              className="h-5 w-5 text-indigo-600 focus:ring-indigo-500 border-gray-300 rounded"
            />
            <label htmlFor="availableOnly" className="text-sm font-medium text-gray-700">
              Show available only
            </label>
          </div>
        </div>
        
        <div className="mt-4 text-sm text-gray-600">
          Showing {currentProducts.length} of {filteredProducts.length} products
          {totalPages > 1 && (
            <span className="ml-2 text-indigo-600">
              (Page {currentPage} of {totalPages})
            </span>
          )}
        </div>
      </div>

      {/* Products Grid */}
      {filteredProducts.length === 0 ? (
        <div className="bg-white rounded-2xl shadow-lg p-12 text-center">
          <div className="text-8xl mb-6 opacity-50">
            {products.length === 0 ? "üì¶" : "üîç"}
          </div>
          <h3 className="text-2xl font-bold text-gray-900 mb-3">
            {products.length === 0 ? "No products available" : "No products found"}
          </h3>
          <p className="text-gray-600 text-lg mb-4">
            {products.length === 0 
              ? "Products added in admin panel will appear here" 
              : "Try adjusting your search criteria"}
          </p>
          <div className="text-sm text-gray-500">
            <p>Debug info: Total products in database: {products.length}</p>
            <p>Backend URL: {process.env.REACT_APP_API_URL || "http://localhost:5000/api"}</p>
          </div>
          {searchTerm && (
            <button
              onClick={() => {
                setSearchTerm("");
                setShowAvailableOnly(false);
              }}
              className="mt-4 text-indigo-600 hover:text-indigo-800 font-medium"
            >
              Clear search
            </button>
          )}
        </div>
      ) : (
        <>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {currentProducts.map((product) => (
              <ProductCard key={product._id} product={product} />
            ))}
          </div>

          {/* Pagination Controls */}
          {totalPages > 1 && (
            <div className="bg-white rounded-2xl shadow-lg p-6 mt-8">
              <div className="flex items-center justify-between">
                <div className="text-sm text-gray-600">
                  Showing {startIndex + 1} to {Math.min(endIndex, filteredProducts.length)} of {filteredProducts.length} products
                </div>
                
                <div className="flex items-center space-x-2">
                  {/* Previous Button */}
                  <button
                    onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                    disabled={currentPage === 1}
                    className="px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                  >
                    ‚Üê Previous
                  </button>

                  {/* Page Numbers */}
                  <div className="flex items-center space-x-1">
                    {[...Array(totalPages)].map((_, index) => {
                      const pageNumber = index + 1;
                      const isCurrentPage = pageNumber === currentPage;
                      
                      // Show first page, last page, current page, and pages around current page
                      const showPage = 
                        pageNumber === 1 ||
                        pageNumber === totalPages ||
                        Math.abs(pageNumber - currentPage) <= 1;

                      if (!showPage) {
                        // Show ellipsis for gaps
                        if (pageNumber === 2 && currentPage > 4) {
                          return <span key={pageNumber} className="px-2 text-gray-500">...</span>;
                        }
                        if (pageNumber === totalPages - 1 && currentPage < totalPages - 3) {
                          return <span key={pageNumber} className="px-2 text-gray-500">...</span>;
                        }
                        return null;
                      }

                      return (
                        <button
                          key={pageNumber}
                          onClick={() => setCurrentPage(pageNumber)}
                          className={`px-3 py-2 rounded-lg transition-colors ${
                            isCurrentPage
                              ? 'bg-indigo-600 text-white'
                              : 'border border-gray-300 hover:bg-gray-50'
                          }`}
                        >
                          {pageNumber}
                        </button>
                      );
                    })}
                  </div>

                  {/* Next Button */}
                  <button
                    onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                    disabled={currentPage === totalPages}
                    className="px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                  >
                    Next ‚Üí
                  </button>
                </div>
              </div>
            </div>
          )}
        </>
      )}

      
    </div>
  );
}
