import { useEffect, useState } from "react";
import API from "../api";
import ProductCard from "../components/ProductCard";

export default function ProductCatalog() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [showAvailableOnly, setShowAvailableOnly] = useState(false);
  const [showOnOfferOnly, setShowOnOfferOnly] = useState(false);
  const [error, setError] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const productsPerPage = 20;

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      setError(null);
      const res = await API.get("/products");

      // Normalize available and onOffer fields to booleans
      const normalizedProducts = res.data.map((p) => ({
        ...p,
        available: p.available === true || p.available === "true",
        onOffer: p.onOffer === true || p.onOffer === "true",
      }));

      setProducts(normalizedProducts);
    } catch (error) {
      console.error("Error fetching products:", error);
      setError(`Failed to load products: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  // Filter products
  const filteredProducts = products.filter((product) => {
    const matchesSearch =
      product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      (product.description &&
        product.description.toLowerCase().includes(searchTerm.toLowerCase()));

    const matchesAvailability = !showAvailableOnly || product.available;
    const matchesOnOffer = !showOnOfferOnly || product.onOffer;

    return matchesSearch && matchesAvailability && matchesOnOffer;
  });

  // Pagination
  const totalPages = Math.ceil(filteredProducts.length / productsPerPage);
  const startIndex = (currentPage - 1) * productsPerPage;
  const endIndex = startIndex + productsPerPage;
  const currentProducts = filteredProducts.slice(startIndex, endIndex);

  const availableCount = products.filter((p) => p.available).length;
  const offerCount = products.filter((p) => p.onOffer).length;

  // Reset to first page when filters/search change
  useEffect(() => {
    setCurrentPage(1);
  }, [searchTerm, showAvailableOnly, showOnOfferOnly]);

  if (loading) {
    return (
      <div className="flex flex-col items-center justify-center h-64">
        <div className="w-16 h-16 mb-4 border-b-4 border-indigo-600 rounded-full animate-spin"></div>
        <p className="font-medium text-gray-600">Loading products...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-8 text-center border border-red-200 bg-red-50 rounded-xl">
        <div className="mb-4 text-6xl">⚠️</div>
        <h2 className="mb-2 text-2xl font-bold text-red-800">Connection Error</h2>
        <p className="mb-4 text-red-600">{error}</p>
        <button
          onClick={fetchProducts}
          className="px-6 py-2 text-white bg-red-600 rounded-lg hover:bg-red-700"
        >
          Try Again
        </button>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="p-8 text-center bg-white shadow-lg rounded-2xl">
        <h1 className="mb-4 text-4xl font-bold text-gray-900">Welcome to Nadir Brothers</h1>
        <p className="mb-6 text-xl text-gray-600">Discover our amazing collection of products</p>
        <div className="flex flex-wrap justify-center gap-4 text-sm font-medium">
          <div className="px-4 py-2 rounded-lg bg-blue-50">
            <span className="text-blue-600">📦 {products.length} Total Products</span>
          </div>
          <div className="px-4 py-2 rounded-lg bg-green-50">
            <span className="text-green-600">✅ {availableCount} Available Now</span>
          </div>
          <div className="px-4 py-2 rounded-lg bg-yellow-50">
            <span className="text-yellow-600">🏷️ {offerCount} On Offer</span>
          </div>
        </div>
      </div>

      {/* Search & Filters */}
      <div className="flex flex-col items-center gap-4 p-6 bg-white shadow-lg rounded-2xl md:flex-row">
        <div className="relative flex-1">
          <div className="absolute inset-y-0 left-0 flex items-center pl-3 pointer-events-none">
            <span className="text-xl text-gray-400">🔍</span>
          </div>
          <input
            type="text"
            placeholder="Search products..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full py-3 pl-10 pr-4 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-transparent"
          />
        </div>
        <div className="flex items-center space-x-6">
          <div className="flex items-center space-x-2">
            <input
              type="checkbox"
              checked={showAvailableOnly}
              onChange={(e) => setShowAvailableOnly(e.target.checked)}
              className="w-5 h-5 text-indigo-600 border-gray-300 rounded focus:ring-indigo-500"
            />
            <label className="text-sm font-medium text-gray-700">Show available only</label>
          </div>
          <div className="flex items-center space-x-2">
            <input
              type="checkbox"
              checked={showOnOfferOnly}
              onChange={(e) => setShowOnOfferOnly(e.target.checked)}
              className="w-5 h-5 text-yellow-500 border-gray-300 rounded focus:ring-yellow-500"
            />
            <label className="text-sm font-medium text-gray-700">Show on offer only</label>
          </div>
        </div>
      </div>

      {/* Products Grid */}
      {filteredProducts.length === 0 ? (
        <div className="p-12 text-center bg-white shadow-lg rounded-2xl">
          <div className="mb-6 opacity-50 text-8xl">{products.length === 0 ? "📦" : "🔍"}</div>
          <h3 className="mb-3 text-2xl font-bold text-gray-900">
            {products.length === 0 ? "No products available" : "No products found"}
          </h3>
          <p className="mb-4 text-lg text-gray-600">
            {products.length === 0
              ? "Products added in admin panel will appear here"
              : "Try adjusting your search or filters"}
          </p>
        </div>
      ) : (
        <>
          <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4">
            {currentProducts.map((product) => (
              <ProductCard key={product._id} product={product} />
            ))}
          </div>

          {/* Pagination */}
          {totalPages > 1 && (
            <div className="flex items-center justify-between p-6 mt-8 bg-white shadow-lg rounded-2xl">
              <div className="text-sm text-gray-600">
                Showing {startIndex + 1} to {Math.min(endIndex, filteredProducts.length)} of{" "}
                {filteredProducts.length} products
              </div>
              <div className="flex items-center space-x-2">
                <button
                  onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
                  disabled={currentPage === 1}
                  className="px-4 py-2 border rounded-lg hover:bg-gray-50 disabled:opacity-50"
                >
                  ← Previous
                </button>
                {[...Array(totalPages)].map((_, index) => {
                  const pageNumber = index + 1;
                  const isCurrent = pageNumber === currentPage;
                  return (
                    <button
                      key={pageNumber}
                      onClick={() => setCurrentPage(pageNumber)}
                      className={`px-3 py-2 rounded-lg ${
                        isCurrent ? "bg-indigo-600 text-white" : "border border-gray-300 hover:bg-gray-50"
                      }`}
                    >
                      {pageNumber}
                    </button>
                  );
                })}
                <button
                  onClick={() => setCurrentPage((prev) => Math.min(prev + 1, totalPages))}
                  disabled={currentPage === totalPages}
                  className="px-4 py-2 border rounded-lg hover:bg-gray-50 disabled:opacity-50"
                >
                  Next →
                </button>
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
}
