"use client"

import { useNavigate } from "react-router-dom"
import { Package, Tag, ArrowRight, CheckCircle, XCircle } from "lucide-react"

export default function ProductCard({ product }) {
  const navigate = useNavigate()

  const handleCardClick = () => {
    navigate(`/product/${product._id}`)
  }

  return (
    <div
      onClick={handleCardClick}
      className="w-full max-w-md transition-all duration-300 transform bg-white border border-gray-100 shadow-lg cursor-pointer md:max-w-lg lg:max-w-xl rounded-xl hover:shadow-2xl hover:-translate-y-2 hover:border-indigo-200"
    >
      {/* Product Image */}
      <div className="relative overflow-hidden rounded-t-xl">
        {product.image ? (
          <img
            src={product.image || "/placeholder.svg"}
            alt={product.name}
            className="object-cover w-full h-56 transition-transform duration-300 hover:scale-110"
          />
        ) : (
          <div className="flex items-center justify-center w-full h-56 bg-gradient-to-br from-indigo-50 to-purple-50">
            <div className="text-center text-gray-400">
              <Package className="w-12 h-12 mx-auto mb-2 text-gray-300" />
              <p className="text-sm font-medium">No Image Available</p>
            </div>
          </div>
        )}

        {/* Availability Badge */}
        <div className="absolute flex gap-2 top-4 right-4">
          <span
            className={`inline-flex items-center gap-1.5 px-3 py-1.5 rounded-full text-xs font-semibold shadow-lg backdrop-blur-sm ${
              product.available ? "bg-green-500/90 text-white" : "bg-red-500/90 text-white"
            }`}
          >
            {product.available ? (
              <>
                <CheckCircle className="w-3 h-3" />
                Available
              </>
            ) : (
              <>
                <XCircle className="w-3 h-3" />
                Out of Stock
              </>
            )}
          </span>

          {/* On Offer Badge */}
          {product.onOffer && (
            <span className="inline-flex items-center gap-1.5 px-3 py-1.5 text-xs font-semibold text-amber-900 bg-amber-400/90 rounded-full shadow-lg backdrop-blur-sm">
              <Tag className="w-3 h-3" />
              On Offer
            </span>
          )}
        </div>
      </div>

      {/* Product Info */}
      <div className="p-6 space-y-4">
        <div>
          <h3 className="mb-2 text-xl font-bold text-gray-900 line-clamp-1">{product.name}</h3>

          {product.description && (
            <p className="h-10 mb-3 text-sm leading-relaxed text-gray-600 line-clamp-2">{product.description}</p>
          )}
        </div>

        {product.category && (
          <div className="flex items-center gap-2 px-3 py-2 rounded-lg bg-indigo-50">
            <Tag className="w-4 h-4 text-indigo-600" />
            <span className="text-sm font-medium text-indigo-700">{product.category}</span>
          </div>
        )}

        {/* Price */}
        <div className="flex items-center justify-between pt-2 border-t border-gray-100">
          <div>
            <span className="text-2xl font-bold text-indigo-600">
              {product.price ? `Rs. ${product.price}` : "Contact for price"}
            </span>
          </div>
        </div>

        {/* CTA */}
        <div className="flex items-center justify-between pt-2">
          <span className="text-sm font-medium text-indigo-600">Click to view details</span>
          <ArrowRight className="w-5 h-5 text-indigo-600 transition-transform duration-200 group-hover:translate-x-1" />
        </div>
      </div>
    </div>
  )
}
