// src/pages/Checkout.js
import React, { useState, useEffect } from "react";
import api from "../api";

export default function Checkout() {
  const [cartItems, setCartItems] = useState([]);
  const [formData, setFormData] = useState({
    name: "",
    city: "",
    address: "",
    contact: "",
    paymentMethod: "COD",
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  // Load cart items from localStorage on mount
  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCartItems(storedCart);
  }, []);

  // Handle form input
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Submit order
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      if (!cartItems || cartItems.length === 0) {
        setMessage("Your cart is empty ❌");
        setLoading(false);
        return;
      }

      const orderData = {
        client: {
          name: formData.name,
          city: formData.city,
          address: formData.address,
          contact: formData.contact,
        },
        products: cartItems.map((item) => ({
          productId: item._id, // must be MongoDB product _id
          name: item.name,
          price: item.price,
          quantity: item.quantity,
        })),
        totalPrice: cartItems.reduce(
          (acc, item) => acc + item.price * item.quantity,
          0
        ),
        paymentMethod: formData.paymentMethod || "COD",
      };

      const res = await api.post("/orders", orderData);
      setMessage(res.data.message || "Order placed successfully ✅");
      console.log("✅ Order placed:", res.data);

      // Clear cart on success
      localStorage.removeItem("cart");
      setCartItems([]);
    } catch (error) {
      console.error("❌ Order submission failed:", error.response?.data || error.message);
      setMessage(error.response?.data?.message || "Failed to place order ❌");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-lg p-6 mx-auto bg-white rounded shadow">
      <h2 className="mb-4 text-xl font-bold">Checkout</h2>

      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          name="name"
          placeholder="Full Name"
          value={formData.name}
          onChange={handleChange}
          required
          className="w-full p-2 border rounded"
        />
        <input
          type="text"
          name="city"
          placeholder="City"
          value={formData.city}
          onChange={handleChange}
          required
          className="w-full p-2 border rounded"
        />
        <input
          type="text"
          name="address"
          placeholder="Address"
          value={formData.address}
          onChange={handleChange}
          required
          className="w-full p-2 border rounded"
        />
        <input
          type="text"
          name="contact"
          placeholder="Contact Number"
          value={formData.contact}
          onChange={handleChange}
          required
          className="w-full p-2 border rounded"
        />

        <select
          name="paymentMethod"
          value={formData.paymentMethod}
          onChange={handleChange}
          className="w-full p-2 border rounded"
        >
          <option value="COD">Cash on Delivery</option>
          <option value="Online">Online Payment</option>
        </select>

        <button
          type="submit"
          disabled={loading}
          className="w-full p-2 text-white bg-blue-600 rounded hover:bg-blue-700"
        >
          {loading ? "Placing Order..." : "Place Order"}
        </button>
      </form>

      {message && (
        <p className="mt-4 font-medium text-center">{message}</p>
      )}
    </div>
  );
}
