import React, { useState } from "react";
import { useCart } from "../context/CartContext";
import api from "api";
import { useNavigate } from "react-router-dom";

export default function Checkout() {
  const { cartItems, getTotalPrice, clearCart } = useCart();
  const [client, setClient] = useState({
    name: "",
    email: "",
    phone: "",
    address: "",
  });
  const [paymentMethod, setPaymentMethod] = useState("COD");
  const navigate = useNavigate();

  const handleChange = (e) => {
    setClient({ ...client, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const orderData = {
      client,
      products: cartItems.map((item) => ({
        product: item._id, // backend expects product ID
        quantity: item.quantity,
      })),
      totalPrice: getTotalPrice(),
      paymentMethod,
    };

    console.log("üì¶ Final orderData:", orderData);

    try {
      const res = await api.post("/orders", orderData);
      console.log("‚úÖ Order success:", res.data);

      clearCart();
      navigate("/success");
    } catch (err) {
      console.error("‚ùå Order submission failed:", err.response?.data || err);
      alert(err.response?.data?.message || "Order failed");
    }
  };

  return (
    <div className="max-w-xl p-6 mx-auto">
      <h1 className="mb-4 text-2xl font-bold">Checkout</h1>

      {cartItems.length === 0 ? (
        <p>Your cart is empty ‚ùå</p>
      ) : (
        <form onSubmit={handleSubmit} className="space-y-4">
          <input
            type="text"
            name="name"
            placeholder="Full Name"
            value={client.name}
            onChange={handleChange}
            required
            className="w-full p-2 border rounded"
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={client.email}
            onChange={handleChange}
            required
            className="w-full p-2 border rounded"
          />
          <input
            type="tel"
            name="phone"
            placeholder="Phone"
            value={client.phone}
            onChange={handleChange}
            required
            className="w-full p-2 border rounded"
          />
          <textarea
            name="address"
            placeholder="Address"
            value={client.address}
            onChange={handleChange}
            required
            className="w-full p-2 border rounded"
          ></textarea>

          <div>
            <label>
              <input
                type="radio"
                name="paymentMethod"
                value="COD"
                checked={paymentMethod === "COD"}
                onChange={(e) => setPaymentMethod(e.target.value)}
              />
              Cash on Delivery
            </label>
          </div>

          <p className="font-semibold">
            Total: Rs {getTotalPrice().toFixed(2)}
          </p>

          <button
            type="submit"
            className="w-full p-2 text-white bg-blue-600 rounded"
          >
            Place Order
          </button>
        </form>
      )}
    </div>
  );
}
