export default function ProductCard({ product }) {
  return (
    <div className="bg-white rounded-xl shadow-lg hover:shadow-xl transition-all duration-300 transform hover:-translate-y-2 border border-gray-100">
      {/* Product Image */}
      <div className="relative overflow-hidden rounded-t-xl">
        {product.image ? (
          <img
            src={`http://localhost:5000/uploads/${product.image}`}
            alt={product.name}
            className="w-full h-48 object-cover transition-transform duration-300 hover:scale-110"
          />
        ) : (
          <div className="w-full h-48 bg-gradient-to-br from-gray-100 to-gray-200 flex items-center justify-center">
            <div className="text-center text-gray-400">
              <div className="text-4xl mb-2">üì¶</div>
              <p className="text-sm">No Image</p>
            </div>
          </div>
        )}
        
        {/* Status Badge */}
        <div className="absolute top-3 right-3">
          <span className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-medium shadow-lg ${
            product.isAvailable 
              ? 'bg-green-500 text-white' 
              : 'bg-red-500 text-white'
          }`}>
            {product.isAvailable ? "‚úÖ Available" : "‚ùå Out of Stock"}
          </span>
        </div>
      </div>
      
      {/* Product Info */}
      <div className="p-6">
        <h3 className="text-xl font-bold text-gray-900 mb-3 line-clamp-1">
          {product.name}
        </h3>
        
        {product.description && (
          <p className="text-gray-600 text-sm mb-4 line-clamp-2 h-10">
            {product.description}
          </p>
        )}
        
        {product.price && (
          <div className="mb-4">
            <span className="text-2xl font-bold text-green-600">
              ${parseFloat(product.price).toFixed(2)}
            </span>
          </div>
        )}
        
        {/* Action Button */}
        <button
          className={`w-full py-3 px-4 rounded-lg font-medium transition-all duration-200 ${
            product.isAvailable
              ? 'bg-gradient-to-r from-indigo-600 to-purple-600 text-white hover:from-indigo-700 hover:to-purple-700 focus:outline-none focus:ring-2 focus:ring-indigo-500'
              : 'bg-gray-300 text-gray-500 cursor-not-allowed'
          }`}
          disabled={!product.isAvailable}
        >
          {product.isAvailable ? 'üõí Contact for Purchase' : '‚ùå Out of Stock'}
        </button>
      </div>
    </div>
  );
}
