import axios from "axios";

// Use environment variable for production, fallback to localhost for development
const API = axios.create({
  baseURL: process.env.REACT_APP_API_URL || "http://localhost:5000/api",
  timeout: 30000,
  headers: {
    "Content-Type": "application/json",
  },
});

// Request interceptor
API.interceptors.request.use(
  (config) => {
    console.log("üåê Client making request to:", config.baseURL + config.url);
    console.log("üîß Environment:", process.env.NODE_ENV);
    return config;
  },
  (error) => {
    console.error("‚ùå Client request error:", error);
    return Promise.reject(error);
  }
);

// Response interceptor
API.interceptors.response.use(
  (response) => {
    console.log("‚úÖ Client API Response received:", response.status);
    return response;
  },
  (error) => {
    console.error("‚ùå Client API Error:", error);
    console.error("‚ùå Error response:", error.response?.data);

    if (error.code === "ECONNABORTED") {
      console.error("‚è∞ Request timeout - Server might be slow");
    }
    if (error.code === "ERR_NETWORK") {
      console.error("üåê Network Error: Check server availability");
    }
    return Promise.reject(error);
  }
);

export default API;
