import React, { useState } from "react";
import { useCart } from "../context/CartContext"; // üëà use your context
import api from "../api";

export default function Checkout() {
  const { cartItems, clearCart, getTotalPrice } = useCart(); // üëà directly get cart
  const [formData, setFormData] = useState({
    name: "",
    city: "",
    address: "",
    contact: "",
    paymentMethod: "COD",
  });

  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      if (!cartItems || cartItems.length === 0) {
        setMessage("Your cart is empty ‚ùå");
        setLoading(false);
        return;
      }

      const orderData = {
        client: { ...formData },
        products: cartItems.map((item) => ({
          productId: item._id,
          name: item.name,
          price: item.price,
          quantity: item.quantity,
        })),
        totalPrice: getTotalPrice(),
        paymentMethod: formData.paymentMethod,
      };

      const res = await api.post("/orders", orderData);
      setMessage(res.data.message || "Order placed successfully ‚úÖ");
      console.log("‚úÖ Order placed:", res.data);

      clearCart(); // empty cart after order
    } catch (error) {
      console.error("‚ùå Order submission failed:", error.response?.data || error.message);
      setMessage(error.response?.data?.message || "Failed to place order ‚ùå");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-lg p-6 mx-auto bg-white rounded shadow">
      <h2 className="mb-4 text-xl font-bold">Checkout</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input name="name" value={formData.name} onChange={handleChange} placeholder="Full Name" required className="w-full p-2 border rounded" />
        <input name="city" value={formData.city} onChange={handleChange} placeholder="City" required className="w-full p-2 border rounded" />
        <input name="address" value={formData.address} onChange={handleChange} placeholder="Address" required className="w-full p-2 border rounded" />
        <input name="contact" value={formData.contact} onChange={handleChange} placeholder="Contact Number" required className="w-full p-2 border rounded" />

        <select name="paymentMethod" value={formData.paymentMethod} onChange={handleChange} className="w-full p-2 border rounded">
          <option value="COD">Cash on Delivery</option>
          <option value="Online">Online Payment</option>
        </select>

        <button type="submit" disabled={loading} className="w-full p-2 text-white bg-blue-600 rounded hover:bg-blue-700">
          {loading ? "Placing Order..." : "Place Order"}
        </button>
      </form>

      {message && <p className="mt-4 font-medium text-center">{message}</p>}
    </div>
  );
}
